import {useEffect, useState} from "react";
import classes from "./Admin.module.css";
import Axios from "axios";


const Admin = (props) => {

    const [title, setTitle] = useState('')
    const [text, setText] = useState('')
    const [image, setImage] = useState('')

    const saveNews = () => {
        Axios.post("http://localhost:3006/api/add_news", {
            title: title,
            text: text,
            image: image
        }).then(()=>{
            alert('successfull insert')
        })
    }

    useEffect(() => {
        Axios.get("http://localhost:3006/api/get_news")
        props.setActive("admin")
    },[])

    return <div className={classes.container}>
        <h1>News posten</h1>
        <div className={classes.form}>
            <label>Title</label>
            <input type={"text"} name={"title"} onChange={(e) => {
            setTitle(e.target.value)}
            }/>
            <label>Text</label>
            <input type={"text"} name={"text"} onChange={(e) => {
            setText(e.target.value)}
            }/>
            <label>Bild URL</label>
            <input type={"text"} name={"image"} onChange={(e) => {
            setImage(e.target.value)}
            }/>
            <button onClick={saveNews}>Posten</button>
        </div>
    </div>
}

export default Admin
