import {useEffect, useState} from "react";
import classes from "./Admin.module.css";
import Axios from "axios";
import SingleNews from "../components/Home_Components/SingleNews";


const Admin = (props) => {

    const [title, setTitle] = useState('')
    const [text, setText] = useState('')
    const [image, setImage] = useState('')

    const [newsList, setNewsList] = useState([])

    const saveNews = () => {
        Axios.post("http://localhost:3006/api/add_news", {
            title: title,
            text: text,
            image: image
        }).then(()=>{
            setNewsList([...newsList,{
                title: title,
                text: text,
                image: image
            }])
        })
    }

    const loadNews = () => {
        Axios.get("http://localhost:3006/api/get_news").then((response)=>{
            setNewsList(response.data)
        })
    }

    const deleteNews = (id) => {
        Axios.delete(`http://localhost:3006/api/delete_news/${id}`).then((response)=>{
            console.log(response)
        })
    }

    const editNews = (id) => {
        Axios.put("http://localhost:3006/api/edit_news", {
            id: id,
            title: title,
            text: text,
            image: image
        }).then((response)=>{
            setNewsList(response.data)
        })
    }

    useEffect(() => {
        props.setActive("admin")
    },[])

    useEffect(()=>{
        loadNews()
    },[])

    return <div className={classes.container}>
        <h1>News posten</h1>
        <div className={classes.form}>
            <label>Title</label>
            <input type={"text"} name={"title"} onChange={(e) => {
            setTitle(e.target.value)}
            }/>
            <label>Text</label>
            <input type={"text"} name={"text"} onChange={(e) => {
            setText(e.target.value)}
            }/>
            <label>Bild URL</label>
            <input type={"text"} name={"image"} onChange={(e) => {
            setImage(e.target.value)}
            }/>
            <button onClick={saveNews}>Posten</button>
        </div>
        <div className={classes.newsBoxes}>
            {
                newsList.map((singleNews, index)=> {
                    return (
                        <div key={index} className={classes.newsCard}>
                            <div>
                                <button onClick={()=>{deleteNews(singleNews.id)}}>LÃ¶schen</button>
                                <button onClick={()=>{editNews(singleNews.id)}}>Bearbeiten</button>
                            </div>
                            <SingleNews
                                id={singleNews.id}
                                title={singleNews.title}
                                text={singleNews.text}
                                imageURL={singleNews.img_url}
                                key={singleNews.id}
                            />
                        </div>
                    )
                })
            }
        </div>
    </div>
}

export default Admin
